["custom"] = "function(event, _, countdown)\n    -- Send information about yourself\n    aura_env.SendPlayerInfo(event)\n    -- Update playermark\n    aura_env.UpdatePlayerMarks()\n    \n    if event == \"CHALLENGE_MODE_START\" then \n        -- Add Seasonal Mobs to table\n        WeakAuras.ScanEvents(\"AutomarkGetMobsRequest\", C_MythicPlus.GetCurrentSeason())\n    elseif event == \"START_TIMER\" and countdown > 0 and not select(3, C_ChallengeMode.GetActiveKeystoneInfo()) then \n        return true\n    end\nend\n\n-- GROUP_ROSTER_UPDATE, READY_CHECK, CHALLENGE_MODE_START, PARTY_MEMBER_ENABLE, START_TIMER",
["custom_type"] = "event",
["custom_hide"] = "timed",
["duration"] = "10",
["events"] = "GROUP_ROSTER_UPDATE, READY_CHECK, CHALLENGE_MODE_START, PARTY_MEMBER_ENABLE, START_TIMER, PARTY_LEADER_CHANGED, PLAYER_TALENT_UPDATE",
["debuffType"] = "HELPFUL",
},
