["customVariables"] = "{\n    glow=\"bool\",\n    meetRequirements=\"bool\",\n    type=\"number\",\n}",
["event"] = "Health",
["subeventPrefix"] = "SPELL",
["unit"] = "player",
["custom"] = "function(states, event, ...)\n    local changed = false\n    if event == \"STATUS\" or event == \"OPTIONS\" then\n        for _, profession in pairs(aura_env.GetProfessions()) do\n            changed = profession:FillState(states) or changed\n            for _, item in pairs(profession:GetAvailableItems()) do\n                changed = item:FillState(states) or changed\n            end\n        end\n        WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", nil, changed)\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, _, spellId = ...\n        \n        local item = aura_env.FindItemBySpellId(spellId)\n        if item then\n            -- Wow api does not reflect that the questId was completed at this point in time\n            C_Timer.After(0.5, function() WeakAuras.ScanEvents(\"MKPT_DELAYED_ITEM_UPDATE\", item) end)\n        end\n    elseif event == \"MKPT_CELL_CLICKED\" then\n        local stateId, _ = ...\n        local state = states[stateId]\n        \n        -- Collapse/Expand if it's a quest cell\n        if state and state.type == 0 then\n            local profession = aura_env.FindProfessionById(stateId)\n            profession.expanded = not profession.expanded\n            for _, item in pairs(profession:GetAvailableItems()) do\n                item:FillState(states)\n            end\n            return true\n        end\n        \n        local itemToTrack = aura_env.FindEntryById(stateId)\n        local previouslyTrackedItem = itemToTrack:ToggleTrack()\n        if previouslyTrackedItem then\n            changed = previouslyTrackedItem:FillState(states) or changed\n        end\n        changed = itemToTrack:FillState(states) or changed\n        \n    elseif event ==\"SKILL_LINE_SPECS_RANKS_CHANGED\" or event== \"SKILL_LINE_SPECS_UNLOCKED\" then\n        for _, profession in pairs(aura_env.GetProfessions()) do\n            changed = profession:FillState(states) or changed\n        end        \n    elseif event == \"SKILL_LINES_CHANGED\" then\n        local missingProfession = aura_env.RemoveMissingProfession()\n        if not missingProfession then \n            return false\n        end\n        states[missingProfession.id] = { changed=true, show = false }\n        for _, item in pairs(missingProfession:GetAvailableItems()) do\n            states[item:GetId()] = { changed=true, show = false }\n        end\n        WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", nil)\n        return true\n    elseif event == \"LEARNED_SPELL_IN_SKILL_LINE\" then\n        local spellId, professionSlotId, _ = ...\n        local profession = aura_env.FindProfessionBySpellId(spellId)\n        \n        if not professionSlotId or not profession then \n            return false\n        end\n        \n        for _, profession in pairs(aura_env.GetProfessions(true)) do\n            profession:FillState(states)\n            for _, item in pairs(profession:GetAvailableItems()) do\n                item:FillState(states)\n            end\n        end\n        WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", nil)\n        return true\n    elseif event == \"GET_ITEM_INFO_RECEIVED\" then\n        local itemId, success, _ = ...\n        if not success then return false end\n        \n        local item = aura_env.FindEntryById(itemId)\n        if item then\n            changed = item:FillState(states)\n        end\n        changed = aura_env.RefreshTrackedItemName(itemId) or changed\n    elseif event == \"MKPT_DELAYED_ITEM_UPDATE\" then\n        local item = ...\n        if not item then return false end\n        \n        if item.profession:FillState(states) then\n            changed = item:FillState(states) or changed\n            item:Untrack()\n        end\n    elseif event == \"BAG_UPDATE_DELAYED\" or event == \"ZONE_CHANGED_NEW_AREA\" then\n        for _, profession in pairs(aura_env.GetProfessions()) do\n            changed = profession:FillState(states) or changed\n            for _, item in pairs(profession:GetAvailableItems()) do\n                changed = item:FillState(states) or changed\n            end\n        end\n        aura_env.RefreshTrackedItemRequirements()\n    elseif event == \"QUEST_DATA_LOAD_RESULT\" then\n        local questId, success = ...\n        if not success then return false end\n        \n        local entry = aura_env.FindEntryByQuestId(questId)\n        if not entry then return false end\n        \n        changed = entry:FillState(states) or changed\n    elseif event == \"QUEST_TURNED_IN\" then\n        local questId, _ = ...\n        local entry = aura_env.FindEntryByQuestId(questId)\n        \n        if not entry then return false end\n        \n        -- Wow api does not reflect that the questId was completed at this point in time\n        C_Timer.After(0.5, function() WeakAuras.ScanEvents(\"MKPT_DELAYED_ITEM_UPDATE\", entry) end)\n    elseif event == \"CURRENCY_DISPLAY_UPDATE\" then\n        local currencyId, _ = ...\n        local profession = aura_env.FindProfessionByCatchUpCurrencyId(currencyId)\n        \n        if profession then\n            changed = profession:FillState(states) or changed\n            for _, item in pairs(profession:GetAvailableItems()) do\n                changed = item:FillState(states) or changed\n            end\n        end\n    end\n    \n    return changed\n    \nend",
