["actions"] = {
["start"] = {
["custom"] = "local state = aura_env.state\nlocal region = aura_env.region\n\nif not state.mkptbutton then\n    local pool = MKPTFrame.Pool\n    local button, _ = pool:Acquire()\n    DebugPrint(\"Acquired button from pool:\",  pool:GetNumActive(), region.cloneId) \n    button:SetAllPoints(region)\n    button:RegisterForClicks(\"LeftButtonDown\")\n    button:SetScript(\"OnClick\", function(self, mouseButton, down) \n            if down then\n                DebugPrint(\"CellClicked:\", region.cloneId, self:GetID())\n                WeakAuras.ScanEvents(\"MKPT_CELL_CLICKED\", region.cloneId)            \n            end\n    end)\n    button:Show()\n    state.mkptbutton = button\nend",
["do_custom"] = true,
},
["init"] = {
["custom"] = "if not MKPTFrame then\n    CreateFrame(\"Frame\", \"MKPTFrame\", UIParent)\n    DebugPrint(\"Initializing frame pool\")\n    local resetterFunc = function (_, frameToReset)\n        frameToReset:ClearAllPoints()\n        frameToReset:SetID(0)\n        frameToReset:UnregisterAllEvents()\n        frameToReset:Hide()\n    end\n    MKPTFrame.Pool = CreateFramePool(\"Button\", nil, nil, resetterFunc)\nend\n\nlocal MKPT_Item = {}\n\nfunction MKPT_Item:New(item)\n    item = item or {}\n    setmetatable(item, self)\n    self.__index = self\n    \n    return item\nend\n\nfunction MKPT_Item:GetId()\n    return self.itemId\nend\n\nfunction MKPT_Item:IsUnique()\n    return false\nend\n\nfunction MKPT_Item:IsCatchUp()\n    return false\nend\n\nfunction MKPT_Item:Show()\n    return self.profession.expanded and (self.profession:IsLearned() or aura_env.config.showAllProfessions) and self:GetRemainingKnowledgePoints() > 0 \nend\n\nfunction MKPT_Item:GetName()\n    if self.name then return self.name end\n    \n    self.name = select(1, C_Item.GetItemInfo(self.itemId))\n    \n    return self.name\nend\n\nfunction MKPT_Item:GetIcon()\n    if self.icon then return self.icon end\n    \n    self.icon = select(5, C_Item.GetItemInfoInstant(self.itemId))\n    \n    return self.icon\nend\n\nfunction MKPT_Item:GetCategoryIcon()\n    return CreateAtlasMarkup(self.atlasIcon , 16, 16)\nend\n\nfunction MKPT_Item:GetRemainingKnowledgePoints()\n    local questCount = #self.questId\n    \n    for _, questId in ipairs(self.questId) do\n        if C_QuestLog.IsQuestFlaggedCompleted(questId) then\n            questCount = questCount - 1\n        end\n    end\n    \n    return questCount * self.kp\nend\n\nfunction MKPT_Item:MeetRenownRequirements()\n    if not self.renown then return true end\n    \n    local renownLevel = C_MajorFactions.GetCurrentRenownLevel(self.renown.majorFactionId) or 0\n    return renownLevel >= self.renown.levelRequired\nend\n\nfunction MKPT_Item:MeetItemRequirements()\n    for _, v in pairs(self.itemRequirements or {}) do\n        local itemCount = C_Item.GetItemCount(v.id)\n        if v.quantity > itemCount then return false end\n    end\n    return true\nend\n\nfunction MKPT_Item:MeetUnlockRequirements()\n    for _, v in pairs(self.unlockRequirements or {}) do\n        if v:GetRemainingKnowledgePoints() > 0 then return false end\n    end\n    return true\nend\n\nfunction MKPT_Item:MeetCurrencyRequirements()\n    if not self.currency then return true end\n    \n    local currencyInfo = C_CurrencyInfo.GetCurrencyInfo(self.currency.id)\n    return currencyInfo.quantity >= self.currency.quantity\nend\n\nfunction MKPT_Item:MeetRequirements()\n    return self:MeetRenownRequirements() \n    and self:MeetItemRequirements() \n    and self:MeetCurrencyRequirements()\n    and self:MeetUnlockRequirements()\nend\n\nlocal trackedItem = nil\n\nfunction MKPT_Item:ToggleTrack()\n    if self:IsHighlighted() then\n        self:Untrack()\n        return nil\n    end\n    \n    local untracked = trackedItem\n    if untracked then untracked:Untrack() end\n    \n    WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", self:GetDescription(), true)\n    local wp = self.waypoint\n    if wp then  \n        local mapPoint = UiMapPoint.CreateFromCoordinates(wp.map, wp.x, wp.y)\n        C_Map.SetUserWaypoint(mapPoint)\n        C_SuperTrack.SetSuperTrackedUserWaypoint(true)\n        local _, isTomTomLoaded = C_AddOns.IsAddOnLoaded(\"TomTom\")\n        if isTomTomLoaded and TomTom then\n            local itemName = self:GetName() or \"Item not cached\"\n            self.tomtomUid = TomTom:AddWaypoint(wp.map, wp.x, wp.y, {title=itemName, persistent=false, source=\"MKPT_WA\"})\n        end\n    end\n    trackedItem = self\n    return untracked\nend\n\nfunction MKPT_Item:Untrack()\n    if trackedItem ~= self then return end\n    \n    C_SuperTrack.SetSuperTrackedUserWaypoint(false)\n    C_Map.ClearUserWaypoint()\n    if TomTom and self.tomtomUid then\n        TomTom:RemoveWaypoint(self.tomtomUid)\n    end\n    WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", nil, true)\n    trackedItem = nil\nend\n\nfunction MKPT_Item:IsHighlighted()\n    return trackedItem == self\nend\n\nfunction MKPT_Item:GetDescription()\n    if not self.waypoint then\n        return self.text or CreateAtlasMarkup(\"VignetteLoot\", 20, 20)..\" Found on treasures around Khaz Algar\"\n    end\n    local renownText = \"\"\n    if not self:MeetRenownRequirements() then\n        local renownInfo = C_MajorFactions.GetMajorFactionData(self.renown.majorFactionId)\n        if renownInfo then\n            renownText = renownInfo.name..\": \\124cffFF0000\"..tostring(renownInfo.renownLevel)..\"\\124r/\"..tostring(self.renown.levelRequired)..\"\\n\"\n        end\n    end\n    local itemRequirements = \"\"\n    if self.itemRequirements then\n        for _,v in pairs(self.itemRequirements) do\n            local itemName = C_Item.GetItemNameByID(v.id) or \"item not loaded\"\n            local itemCount = C_Item.GetItemCount(v.id)\n            local meetsRequirements = itemCount >= v.quantity\n            if meetsRequirements then\n                itemRequirements = itemRequirements..tostring(itemCount)..\"/\"..tostring(v.quantity)..\" \"..itemName..\"\\n\"\n            else\n                itemRequirements = itemRequirements..WrapTextInColorCode(tostring(itemCount), \"ffff0000\")..\"/\"..tostring(v.quantity)..\" \"..itemName..\"\\n\"\n            end\n        end\n    end\n    \n    local currencyRequirements = \"\"\n    local requiredCurrency = self.currency\n    if requiredCurrency then\n        local currencyInfo = C_CurrencyInfo.GetCurrencyInfo(requiredCurrency.id)\n        local meetsRequirements = currencyInfo.quantity >= requiredCurrency.quantity \n        local currentQuantityStr = meetsRequirements and tostring(currencyInfo.quantity) or WrapTextInColorCode(tostring(currencyInfo.quantity), \"ffff0000\")\n        currencyRequirements = CreateSimpleTextureMarkup(currencyInfo.iconFileID, 16, 16)..\" \"..currencyInfo.name..\": \"..currentQuantityStr..\"/\"..tostring(requiredCurrency.quantity)..\"\\n\"\n    end\n    \n    local name = self.text or self.name or \"Not loaded yet\"\n    local mapInfo = C_Map.GetMapInfo(self.waypoint.map)\n    return string.format(\"%s%s\\n%s - x:%.2f y:%.2f\", renownText..itemRequirements..currencyRequirements, name, mapInfo.name, self.waypoint.x * 100, self.waypoint.y * 100)\nend\n\nfunction MKPT_Item:FillState(states)\n    local state = states[self:GetId()] or {show=false, atlasIcon=self:GetCategoryIcon(), icon=self:GetIcon(), itemId=self.itemId, type=self:GetType(), index=self.index}\n    \n    local remainingPoints = self:GetRemainingKnowledgePoints()\n    local meetsRequirements = self:MeetRequirements()\n    local show = self:Show()\n    \n    if not (show or state.show) then return false end\n    \n    local changed = state.atlasIcon ~= self:GetCategoryIcon()\n    or state.glow ~= self:IsHighlighted()\n    or state.meetRequirements ~= meetsRequirements\n    or state.name ~= self:GetName() \n    or state.remainingPoints ~= remainingPoints\n    or state.show ~= show \n    \n    if not changed then return false end\n    \n    state.changed = true\n    state.atlasIcon=self:GetCategoryIcon()\n    state.glow = self:IsHighlighted()\n    state.meetRequirements = meetsRequirements\n    state.name = self:GetName()\n    state.remainingPoints = remainingPoints\n    state.show = show\n    \n    states[self:GetId()] = state\n    \n    return true\nend\n\nlocal MKPT_CatchUp = MKPT_Item:New()\n\n\nfunction MKPT_CatchUp:GetRemainingKnowledgePoints()\n    local weekly = self.profession:GetCatchUpCurrencyLeft()\n    for _, requirement in ipairs(self.unlockRequirements) do\n        weekly = weekly - requirement:GetRemainingKnowledgePoints()\n    end\n    return weekly\nend\n\nfunction MKPT_CatchUp:GetType()\n    return 4\nend\n\nfunction MKPT_CatchUp:GetName()\n    if self.text then return self.text end\n    return MKPT_Item.GetName(self)\nend\n\nfunction MKPT_CatchUp:IsCatchUp()\n    return true\nend\n\nfunction MKPT_CatchUp:Show()\n    return not aura_env.config.hideCatchUp and MKPT_Item.Show(self)\nend\n\n\nfunction MKPT_CatchUp:MeetRequirements()\n    return self.text ~= nil or MKPT_Item.MeetRequirements(self)\nend\n\n\nfunction MKPT_CatchUp:GetDescription()\n    if self.text then \n        return self.text..\"\\n\"..CreateSimpleTextureMarkup(4914670,16, 16)..\" ~24 hours - \"..CreateSimpleTextureMarkup(5976939,16, 16)..\" ~84 hours\"\n    end\n    \n    local requirements = {}\n    local description = \"Finish \"\n    for _,v in ipairs(self.unlockRequirements or {}) do \n        local categoryIcon = v:GetCategoryIcon()\n        if not requirements[categoryIcon] then\n            requirements[categoryIcon] = true\n            description = description..categoryIcon\n        end\n    end\n    return description..\" to unlock.\"\nend\n\nlocal MKPT_DarkmoonQuest = MKPT_Item:New()\n\nfunction MKPT_DarkmoonQuest:GetId()\n    return self.questId[1]\nend\n\nfunction MKPT_DarkmoonQuest:GetName()\n    if self.name then return self.name end\n    \n    self.name = C_QuestLog.GetTitleForQuestID(self:GetId())\n    return self.name\nend\n\nfunction MKPT_DarkmoonQuest:GetIcon()\n    -- Darkmoon eye IconId\n    return 1100023\nend\n\nfunction MKPT_DarkmoonQuest:IsDmfUp()\n    local dayOfWeek = tonumber(date(\"%w\"))\n    local dayOfMonth = tonumber(date(\"%e\"))\n    \n    local firstSundayOfMonth = ((dayOfMonth - (dayOfWeek + 1)) % 7) + 1\n    local daysSinceFirstSunday = dayOfMonth - firstSundayOfMonth\n    return daysSinceFirstSunday >= 0 and daysSinceFirstSunday <= 6\nend\n\nfunction MKPT_DarkmoonQuest:GetRemainingKnowledgePoints()\n    return not self:IsDmfUp() and 0 or MKPT_Item.GetRemainingKnowledgePoints(self)\nend\n\nfunction MKPT_DarkmoonQuest:GetCategoryIcon()\n    return CreateAtlasMarkup(\"quest-recurring-available\", 16, 16)\nend\n\nfunction MKPT_DarkmoonQuest:GetType()\n    return 3\nend\n\nlocal MKPT_UniqueBook = MKPT_Item:New()\n\nfunction MKPT_UniqueBook:IsUnique()\n    return  true\nend\n\nfunction MKPT_UniqueBook:GetRemainingKnowledgePoints()\n    if self._done then return 0 end\n    local remainingKp = MKPT_Item.GetRemainingKnowledgePoints(self)\n    self._done = remainingKp == 0\n    return remainingKp\nend\n\nfunction MKPT_UniqueBook:GetCategoryIcon()\n    local atlasIcon = self.atlasIcon or \"Levelup-Icon-Bag\"\n    return CreateAtlasMarkup(atlasIcon, 16, 16)\nend\n\nfunction MKPT_UniqueBook:GetType()\n    return 1\nend\n\nfunction MKPT_UniqueBook:Show()\n    return not aura_env.config.weekly and MKPT_Item.Show(self)\nend\n\nlocal MKPT_UniqueTreasure = MKPT_UniqueBook:New()\n\nfunction MKPT_UniqueTreasure:GetCategoryIcon()\n    local atlasIcon = self.atlasIcon or \"poi-islands-table\"\n    local waypoint = self.waypoint\n    local playerMap = C_Map.GetBestMapForUnit(\"player\")\n    \n    if waypoint and playerMap then\n        local parentMap = C_Map.GetMapInfo(C_Map.GetMapInfo(playerMap).parentMapID or playerMap)\n        playerMap = parentMap.mapType == 3 and parentMap.mapID or playerMap\n        \n        local waypointMap = waypoint.map\n        local parentWaypointMap = C_Map.GetMapInfo(C_Map.GetMapInfo(waypointMap).parentMapID)\n        waypointMap = parentWaypointMap.mapType == 3 and parentWaypointMap.mapID or waypointMap\n        \n        if waypointMap == playerMap then\n            return CreateAtlasMarkup(atlasIcon, 16, 16)\n        end\n    end\n    \n    return CreateAtlasMarkup( \"poi-islands-table\", 16, 16, 0, 0, 64, 64, 64)\nend\n\nlocal MKPT_WeeklyTreasure = MKPT_Item:New()\n\nfunction MKPT_WeeklyTreasure:GetCategoryIcon()\n    local atlasIcon = self.atlasIcon or \"VignetteLoot\"\n    return CreateAtlasMarkup(atlasIcon, 16, 16)\nend\n\nfunction MKPT_WeeklyTreasure:GetType()\n    return 2\nend\n\nlocal MKPT_WeeklyQuestItem = MKPT_Item:New()\n\nfunction MKPT_WeeklyQuestItem:GetRemainingKnowledgePoints()\n    for _, questId in ipairs(self.questId) do\n        if C_QuestLog.IsQuestFlaggedCompleted(questId) then\n            return 0\n        end\n    end\n    \n    return self.kp\nend\n\nfunction MKPT_WeeklyQuestItem:GetCategoryIcon()\n    return CreateAtlasMarkup(\"quest-recurring-available\", 16, 16)\nend\n\nfunction MKPT_WeeklyQuestItem:GetType()\n    return 2\nend\n\nlocal professionSpellIdIdx = {}\nlocal professionCatchUpCurrencyIdIdx = {}\n\nlocal MKPT_Profession = {}\nMKPT_Profession.__index = MKPT_Profession\n\nfunction MKPT_Profession:New(professionId, spellId, catchUpCurrencyId)\n    local profession = {}\n    setmetatable(profession, self)\n    profession.id = professionId\n    profession.spellId = spellId\n    profession.catchUpCurrencyId = catchUpCurrencyId\n    \n    professionSpellIdIdx[spellId] = profession\n    professionCatchUpCurrencyIdIdx[catchUpCurrencyId] = profession\n    \n    local info = C_TradeSkillUI.GetProfessionInfoBySkillLineID(profession.id)\n    profession.name = info.parentProfessionName\n    profession.skillLine = info.parentProfessionID\n    profession.icon =  C_TradeSkillUI.GetTradeSkillTexture(profession.id)\n    profession.expanded = not aura_env.config.minimized\n    profession.entries = {}\n    profession.index = profession.id * 1000\n    return profession\nend\n\nlocal spellIdIdx = {}\nlocal questIdIdx = {}\nlocal idIdx = {}\n\nfunction MKPT_Profession:AddEntry(entry)\n    entry.profession = self\n    if entry:IsUnique() and entry:GetRemainingKnowledgePoints() == 0 then\n        return self\n    end\n    if entry.spell then spellIdIdx[entry.spell] = entry end\n    \n    idIdx[entry:GetId()] = entry\n    \n    for _, questId in ipairs(entry.questId) do\n        questIdIdx[questId] = entry\n    end\n    \n    table.insert(self.entries, entry)\n    entry.index = self.index + #self.entries \n    return self\nend\n\nfunction MKPT_Profession:GetSkillLevel()\n    local prof1, prof2, _ = GetProfessions()\n    if prof1 then\n        local _,_,skillLevel,maxSkillLevel,_,_,skillLine, bonusSkill, _ = GetProfessionInfo(prof1)\n        if skillLine == self.skillLine then \n            return {skillLevel = skillLevel, maxSkillLevel=maxSkillLevel, bonusSkill=bonusSkill}\n        end\n    end\n    if prof2 then\n        local _,_,skillLevel,maxSkillLevel,_,_,skillLine, bonusSkill, _ = GetProfessionInfo(prof2)\n        if skillLine == self.skillLine then \n            return {skillLevel = skillLevel, maxSkillLevel=maxSkillLevel, bonusSkill=bonusSkill}\n        end\n    end\n    \n    return {skillLevel=0, maxSkillLevel=0, bonusSkill=0}\n    \nend\n\nfunction MKPT_Profession:CalculateRemainingKps()\n    local weekly, unique, catchUp = 0, 0, 0\n    for _, item in pairs(self.entries) do\n        local remaining = item:GetRemainingKnowledgePoints()\n        if item:IsUnique() then\n            unique = unique + remaining\n        elseif item:IsCatchUp() then\n            catchUp = catchUp + remaining\n        else\n            weekly = weekly + remaining\n        end\n    end\n    return { weekly=weekly, unique=unique, catchUp=catchUp }\nend\n\nfunction MKPT_Profession:GetCatchUpCurrencyLeft()\n    local currencyInfo = C_CurrencyInfo.GetCurrencyInfo(self.catchUpCurrencyId)\n    return currencyInfo.maxQuantity - currencyInfo.quantity\nend\n\nfunction MKPT_Profession:FillState(states)\n    local state = states[self.id] or { show=false, name = self.name, icon = self.icon, index = self.index, glow = false, type = 0 }\n    \n    local remaining = self:CalculateRemainingKps()\n    local skillLevel = self:GetSkillLevel()\n    local show = (not aura_env.config.hideWhenDone) \n    or (not aura_env.config.weekly and remaining.unique > 0)\n    or (not aura_env.config.hideCatchUp and remaining.catchUp > 0)\n    or (remaining.weekly > 0)\n    \n    if not (show or state.show) then return false end\n    \n    local changed = state.show ~= show \n    or state.skillLevel ~= skillLevel.skillLevel\n    or state.maxSkillLevel ~= skillLevel.maxSkillLevel\n    or state.bonusSkill ~= skillLevel.bonusSkill\n    or state.remainingUniqueKp ~= remaining.unique\n    or state.remainingWeeklyKp ~= remaining.weekly\n    or state.remainingCatchUpKp ~= remaining.catchUp\n    \n    if not changed then \n        return false \n    end\n    \n    state.changed = true\n    state.show = show\n    state.skillLevel = skillLevel.skillLevel\n    state.maxSkillLevel = skillLevel.maxSkillLevel\n    state.bonusSkill = skillLevel.bonusSkill\n    state.remainingUniqueKp = remaining.unique\n    state.remainingWeeklyKp = remaining.weekly\n    state.remainingCatchUpKp = remaining.catchUp\n    state.formattedCatchUp = WrapTextInColorCode(\"+\"..tostring(remaining.catchUp), \"ff00ff00\")\n    \n    states[self.id] = state\n    \n    return true\nend\n\nfunction MKPT_Profession:IsLearned()\n    return aura_env.config.showAllProfessions or IsPlayerSpell(self.spellId)\nend\n\nfunction MKPT_Profession:GetAvailableItems()\n    return self.entries\nend\n\naura_env.FindEntryById = function (entryId)\n    return idIdx[entryId]\nend\n\naura_env.FindEntryByQuestId = function (questId)\n    return questIdIdx[questId]\nend\n\naura_env.FindItemBySpellId = function (spellId)\n    return spellIdIdx[spellId]\nend\n\naura_env.FindProfessionById = function (professionId)\n    return aura_env.GetProfessions()[professionId]\nend\n\naura_env.FindProfessionBySpellId = function (spellId)\n    return professionSpellIdIdx[spellId]\nend\n\naura_env.FindProfessionByCatchUpCurrencyId = function (catchUpCurrencyId)\n    return professionCatchUpCurrencyIdIdx[catchUpCurrencyId]\nend\n\naura_env.RemoveMissingProfession = function ()\n    local professions = aura_env.GetProfessions()\n    for _, profession in pairs(professions) do\n        if not profession:IsLearned() then\n            professions[profession.id] = nil\n            return profession\n        end\n    end\nend\n\n\naura_env.RefreshTrackedItemName = function (requiredItemId)\n    if not trackedItem or not trackedItem.itemRequirements then  \n        return false \n    end\n    for _, itemRequirement in ipairs(trackedItem.itemRequirements) do\n        if itemRequirement.id == requiredItemId then\n            WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", trackedItem:GetDescription(), true)\n            return true\n        end\n    end\n    \n    return false\nend\n\naura_env.RefreshTrackedItemRequirements = function ()\n    if not trackedItem then return end\n    \n    if not trackedItem:Show() then\n        trackedItem:Untrack()\n        return\n    end\n    \n    if trackedItem.itemRequirements then  \n        WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", trackedItem:GetDescription(), true)\n    end\nend\n\nlocal db = {\n    -- Alchemy\n    [2871]=MKPT_Profession:New(2871, 423321, 3057)\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83840}, itemId=226265, waypoint={map=2339, x=0.3245, y=0.6034}, kp=3})  -- \"Earthen Iron Powder\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83841}, itemId=226266, waypoint={map=2248, x=0.5770, y=0.6177}, kp=3})  -- \"Metal Dornogal Frame\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83842}, itemId=226267, waypoint={map=2214, x=0.4218, y=0.2415}, kp=3})  -- \"Reinforced Beaker\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83843}, itemId=226268, waypoint={map=2214, x=0.6496, y=0.6174}, kp=3})  -- \"Engraved Stirring Rod\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83844}, itemId=226269, waypoint={map=2215, x=0.4265, y=0.5510}, kp=3})  -- \"Chemist's Purified Water\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83845}, itemId=226270, waypoint={map=2215, x=0.4166, y=0.5583}, kp=3})  -- \"Sanctified Mortar and Pestle\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83846}, itemId=226271, waypoint={map=2213, x=0.4537, y=0.1322}, kp=3})  -- \"Nerubian Mixing Salts\"\n    :AddEntry(MKPT_UniqueTreasure:New{questId={83847}, itemId=226272, waypoint={map=2255, x=0.4288, y=0.5724}, kp=3})  -