["custom"] = "function(states, ...)    \n    \n    --local playerZone = C_Map.GetBestMapForUnit(\"player\")\n    -- local playerName = UnitName(\"player\")\n    \n    local hideCompleted = aura_env.config.general.hideCompleted\n    \n    states[1] = {\n        show = true,\n        disp = \"Race Name\",\n        normal = \"Normal\",\n        advanced = \"Advanced\",\n        reversed = \"Reversed\",\n        inZone = true,\n        raceZone = \"\"\n    }\n    \n    local idx = 2\n    \n    for _, race in ipairs(aura_env.races) do\n        \n        local name = race.name\n        \n        local normalIcon = \"None\"\n        local advancedIcon = \"None\"\n        local reverseIcon = \"None\"\n        \n        -- Bronze\n        local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achNormGold)\n        if (completed and wasEarnedByMe) then\n            normalIcon = \"gold\"\n        else\n            local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achNormSilver)\n            if (completed and wasEarnedByMe) then\n                normalIcon = \"silver\"\n            else\n                local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achNormBronze)\n                if (completed and wasEarnedByMe) then\n                    normalIcon = \"bronze\"\n                end\n            end\n            \n        end\n        \n        -- Advanced\n        local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achAdvGold)\n        if (completed and wasEarnedByMe) then\n            advancedIcon = \"gold\"\n        else\n            local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achAdvSilver)\n            if (completed and wasEarnedByMe) then\n                advancedIcon = \"silver\"\n            else\n                local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achAdvBronze)\n                if (completed and wasEarnedByMe) then\n                    advancedIcon = \"bronze\"\n                end\n            end\n            \n        end\n        \n        -- Reverse\n        local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achRevGold)\n        if (completed and wasEarnedByMe) then\n            reverseIcon = \"gold\"\n        else\n            local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achRevSilver)\n            if (completed and wasEarnedByMe) then\n                reverseIcon = \"silver\"\n            else\n                local _, _, _, completed, _, _, _, _,_, _, _, _, wasEarnedByMe, _, _ = GetAchievementInfo(race.achRevBronze)\n                if (completed and wasEarnedByMe) then\n                    reverseIcon = \"bronze\"\n                end\n            end\n            \n        end\n        \n        local skip = (hideCompleted and normalIcon == \"gold\" and advancedIcon == \"gold\" and reverseIcon == \"gold\")\n        \n        if skip then\n            states[idx] = {}\n        else  \n            states[idx] = {\n                show = true,\n                disp = name,\n                normal = normalIcon,\n                advanced = advancedIcon,\n                reversed = reverseIcon,\n                inZone = inZone,\n                raceZone = race.zone,\n                index = idx\n            }\n            \n        end\n        \n        idx = idx + 1\n    end\n    \n    return true\nend",
