["custom"] = "aura_env.saybubble = {}\naura_env.sayBubbleOn = aura_env.config.saybubble\naura_env.saybubbletimer = {}\naura_env.sayBubbleTimerOn = aura_env.config.saybubbletimer\naura_env.soundFiles = {}\naura_env.soundchannels = {}\naura_env.soundChannelSelected = aura_env.config.soundchannels\naura_env.lightsay = {}\naura_env.sayBubbleLightOn = aura_env.config.lightsay\naura_env.circlevisual = {}\naura_env.circleVisualOff = aura_env.config.circlevisual\n\n-- Check for Say Bubble Custom Option Selection --\nif aura_env.sayBubbleOn == true then\n    aura_env.sayBubbleOnText = \"{rt6}\"\n    aura_env.sayBubbleOnTextCleared = \"Cleared\"\nelse\n    aura_env.sayBubbleOnText = \"\"\n    aura_env.sayBubbleOnTextCleared = \"\"\nend\n\n-- Check for BigWigs or DBM for audio file --\nif IsAddOnLoaded(\"BigWigs\") then\n    local file = (\"Interface\\\\AddOns\\\\Bigwigs\\\\Media\\\\Sounds\\\\Amy\\\\%d.ogg\")\n    for i=1, 5 do\n        aura_env.soundFiles[i] = file:format(i)\n    end\nelseif IsAddOnLoaded(\"DBM-Core\") then\n    local file = (\"Interface\\\\AddOns\\\\DBM-Core\\\\sounds\\\\Corsica\\\\%d.ogg\")\n    for i=1, 5 do\n        aura_env.soundFiles[i] = file:format(i)\n    end\nelse\n    local file = (\"\")\n    for i=1, 5 do\n        aura_env.soundFiles[i] = file:format(i)\n    end\nend\n\n-- Assign Audio Channel\nif aura_env.soundChannelSelected == 1 then\n    aura_env.soundChannelSelected = \"Master\"\nend\nif aura_env.soundChannelSelected == 2 then\n    aura_env.soundChannelSelected = \"Sound Effects\"\nend\nif aura_env.soundChannelSelected == 3 then\n    aura_env.soundChannelSelected = \"Ambient Sounds\"\nend\nif aura_env.soundChannelSelected == 4 then\n    aura_env.soundChannelSelected = \"Dialog\"\nend\nif aura_env.soundChannelSelected == 5 then\n    aura_env.soundChannelSelected = \"Music\"\nend",
