["customIcon"] = "",
["check"] = "event",
["custom"] = "function(event, unitId)\n    -- Check if the player is a marker or if the event should be handled\n    local playerData = aura_env.partyMembers[aura_env.GetPlayerName()]\n    \n    \n    if not playerData.marker or (event == \"MODIFIER_STATE_CHANGED\" and not UnitExists(\"mouseover\")) then\n        return\n    end\n    \n    -- Determine the relevant unit for the event\n    local unit = (event == \"UPDATE_MOUSEOVER_UNIT\" or event == \"MODIFIER_STATE_CHANGED\") and \"mouseover\" or unitId\n    \n    -- Extract the necessary data\n    local guid = UnitGUID(unit)\n    local npcId = aura_env.GetNpcIdFromGUID(guid)\n    local mobData = aura_env.mobs[npcId]\n    \n    -- Checking that the unit exists in our mobs-table, and that it's not already marked. Else return. \n    if not mobData or aura_env.IsMobAlreadyMarked(guid) then\n        return\n    end\n    \n    -- Get Automark option from mob or Global setting. \n    local automark = mobData.autoMarkOption == 5 and aura_env.config.settings.global.automarkOption or mobData.autoMarkOption\n    \n    -- Check for auto-marking based on event and options\n    if (event == \"UPDATE_MOUSEOVER_UNIT\" or event == \"MODIFIER_STATE_CHANGED\") then\n        -- Auto-mark: Mouseover\n        if not aura_env.ShouldAutoMarkMouseover(unit, automark) or not aura_env.CheckModifierState() or not aura_env.CheckMarkDistance(npcId, unit, guid) then\n            return\n        end\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" or event == \"UNIT_THREAT_LIST_UPDATE\" then\n        -- Auto-mark: Nameplate / Nameplate + Combat\n        if not aura_env.ShouldAutoMarkNameplate(unit, automark, guid) then\n            return\n        end\n    end\n    \n    -- Determine the priority mark type\n    local priorities = mobData.prio\n    \n    -- Initialize mark\n    local mark\n    local check = 1\n    \n    for i = 1, 5 do\n        local priority = priorities[i]\n        \n        if priority == 10 then\n            -- Queue\n            aura_env.AddNpcToQueue(guid, npcId)\n            break\n        elseif priority == 11 or (i == 5 and check == 1) then\n            -- Mark Order\n            mark = aura_env.GetAvailableMarkFromMarkOrder(npcId)\n            if not mark and mobData.notAvailable then\n                -- Add the mob to the general Queue\n                aura_env.AddNpcToQueue(guid, npcId)\n            end\n            break\n        elseif priority > 1 then\n            -- Normal Mark (Skull, Cross, Star, etc)\n            local markToCheck = 10 - priority\n            if aura_env.CheckIfMarkIsAvailable(markToCheck, npcId) then\n                mark = markToCheck\n                break\n            end\n        end\n        check = check * priority\n    end\n    \n    -- Apply mark and announce if applicable\n    if mark then \n        aura_env.ApplyAndAnnounceMark(unit, npcId, guid, mark, event)\n    end\nend\n\n-- UPDATE_MOUSEOVER_UNIT, NAME_PLATE_UNIT_ADDED, UNIT_THREAT_LIST_UPDATE, MODIFIER_STATE_CHANGED",
