["custom"] = "function(event, ...)\n    local _, subEvent, _, _, _, _, _, guid = ...\n    if event == \"INITIALIZE\" then \n        WeakAuras.ScanEvents(\"AUTOMARK_GET_MOBS\", aura_env.GetInstanceID())\n        aura_env.SendRoleCall()\n    elseif event == \"PLAYER_REGEN_ENABLED\" then \n        aura_env.ResetMarksAndQueue()\n        aura_env.UpdatePlayerMarks()\n    elseif event == \"WA_DELAYED_PLAYER_ENTERING_WORLD\" then\n        WeakAuras.ScanEvents(\"AUTOMARK_GET_MOBS\", aura_env.GetInstanceID())\n        aura_env.SendPlayerInfo()\n        aura_env.UpdatePlayerMarks()\n        for mob, v in pairs(aura_env.mobs) do\n            if v.instance ~= aura_env.GetInstanceID() then \n                mob = nil\n            end\n        end\n    elseif (event == \"COMBAT_LOG_EVENT_UNFILTERED\" or event == \"TEST_UNIT_DIED\") and subEvent == \"UNIT_DIED\" then\n        aura_env.ProcessUnitDeath(guid)\n    end\nend",
