["custom"] = "function(event, _, countdown)\n    if event == \"READY_CHECK\" or event == \"CHALLENGE_MODE_START\" or event == \"PARTY_MEMBER_ENABLE\" or event == \"PLAYER_ENTERING_WORLD\" or event == \"GROUP_ROSTER_UPDATE\" then \n        -- Send information about yourself\n        aura_env.SendPlayerInfo()\n        -- Update playermark\n        aura_env.UpdatePlayerMarks()\n    end\n    if event == \"CHALLENGE_MODE_START\" then \n        -- Add Seasonal Mobs to table\n        WeakAuras.ScanEvents(\"AUTOMARK_GET_MOBS\", C_MythicPlus.GetCurrentSeason())\n    elseif event == \"GROUP_ROSTER_UPDATE\" then \n        aura_env.RemoveOldPartyMembers()\n    elseif event == \"START_TIMER\" and countdown > 0 and not select(3, C_ChallengeMode.GetActiveKeystoneInfo()) then \n        return true\n    end\nend\n\n-- GROUP_ROSTER_UPDATE, READY_CHECK, CHALLENGE_MODE_START, PARTY_MEMBER_ENABLE, START_TIMER",
