["custom"] = "function(s, event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local subEvent = select(2, ...)\n        if subEvent == \"SPELL_CAST_START\" then\n            local spellID = select(12, ...)\n            local guid = select(4, ...)\n            if spellID == 409492 then\n                s[guid] = {\n                    show = true,\n                    changed = true,\n                    autoHide = true,\n                    progressType = \"timed\",\n                    duration = 10,\n                    expirationTime = 10 + GetTime(),\n                    icon = 237555,      \n                }\n                return true\n            end\n        elseif subEvent == \"SPELL_DISPEL\" or subEvent == \"SPELL_STOLEN\" then\n            local spellID = select(15, ...)\n            local guid = select(8, ...)\n            if aura_env.afflictedDebuffs[spellID] and s[guid] then\n                s[guid].changed = true\n                s[guid].show = false\n                return true\n            end\n        elseif subEvent == \"SPELL_HEAL\" or subEvent == \"SPELL_PERIODIC_HEAL\" then\n            local guid = select(8, ...)\n            local overhealing = select(16, ...)\n            if s[guid] and overhealing > 0 then\n                s[guid].changed = true\n                s[guid].show = false\n                return true\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_STOP\"  and ... then\n        local unit, _, spellID = ...\n        local guid = UnitGUID(unit)\n        if spellID == 409492 and s[guid] then\n            s[guid].changed = true\n            s[guid].show = false\n            return true\n        end\n    end\nend",
