["custom"] = "function(s, event, triggerNum, triggerStates)\n    if event == \"TRIGGER\" then\n        if triggerNum == 3 then\n            local time = GetTime()\n            s[\"\"] = {\n                show = true,\n                changed = true,\n                expirationTime = 59+GetTime(),\n                progressType = \"timed\",\n                duration = 59,\n                autoHide = true,\n            }\n            if not WeakAuras.GetActiveTriggers(aura_env.id)[2] then\n                local s = s[\"\"]\n                s.remaining = s.expirationTime - GetTime()\n                s.paused = true\n                s.changed = true\n            end\n            return true\n        elseif triggerNum == 2 then\n            local combat = false\n            if next(triggerStates) then\n                combat = true\n            end\n            local s = s[\"\"]\n            if s then\n                if combat and s.paused then\n                    s.paused = false\n                    s.expirationTime = s.remaining + GetTime()\n                    s.changed = true\n                    return true\n                elseif not combat and not s.paused then\n                    s.remaining = s.expirationTime - GetTime()\n                    s.paused = true\n                    s.changed = true\n                    return true\n                end\n            end\n        end\n    elseif event ==\"AFFLICTED_RETRIGGER\" and triggerNum then\n        s[\"\"] = {\n            show = true,\n            changed = true,\n            expirationTime = 59+GetTime(),\n            progressType = \"timed\",\n            duration = 59,\n            autoHide = true,\n        }\n        if not WeakAuras.GetActiveTriggers(aura_env.id)[2] then\n            local s = s[\"\"]\n            s.remaining = s.expirationTime - GetTime()\n            s.paused = true\n            s.changed = true\n        end\n        return true\n    end\nend\n\n\n\n",
