["custom"] = "aura_env.LSpec = LibStub(\"LibSpecialization\")\n\naura_env.order = {\n    [\"MELEE\"] = 1,\n    [\"RANGED\"] = 2,\n}\n\naura_env.split = function(str, pat)\n    local t = {}  -- NOTE: use {n = 0} in Lua-5.0\n    local fpat = \"(.-)\" .. pat\n    local last_end = 1\n    local s, e, cap = str:find(fpat, 1)\n    while s do\n        if s ~= 1 or cap ~= \"\" then\n            table.insert(t, cap)\n        end\n        last_end = e+1\n        s, e, cap = str:find(fpat, last_end)\n    end\n    if last_end <= #str then\n        cap = str:sub(last_end)\n        table.insert(t, cap)\n    end\n    return t\nend",
