["custom"] = "function(allstates, event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID, _,_, auraType = ...\n        if spellID == 401419 then\n            if subEvent == \"SPELL_AURA_APPLIED\" then\n                if aura_env.timer then\n                    aura_env.timer:Cancel()\n                end\n                local state = allstates[\"volatile\"]\n                if state then\n                    state.show = false\n                    state.changed = true\n                end\n                local duration = aura_env.isHM and 4 or 6\n                allstates[\"volatile\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = duration,\n                    expirationTime = duration + GetTime(),\n                }\n                aura_env.timer = C_Timer.NewTicker(duration, function() WeakAuras.ScanEvents(\"CAUSESE_RASHOK\", \"nilcheck\") end)\n                return true  \n            elseif subEvent == \"SPELL_AURA_REMOVED\" then\n                if aura_env.timer then\n                    aura_env.timer:Cancel()\n                end\n                local state = allstates[\"volatile\"]\n                if state then\n                    state.show = false\n                    state.changed = true\n                    return true\n                end\n            end\n        end\n    elseif event == \"CAUSESE_RASHOK\" and ... then\n        local duration = aura_env.isHM and 4 or 6\n        allstates[\"volatile\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = duration,\n            expirationTime = duration + GetTime(),\n        }\n        return true\n    elseif event == \"ENCOUNTER_START\" and aura_env.timer