["custom"] = "aura_env.timers={}\nlocal bar = aura_env.region.bar\nlocal j = 1\nwhile bar[\"spark\"..j] do\n    bar[\"spark\"..j]:Hide()\n    j = j + 1\nend\nj = 1\nwhile bar[\"sparktext\"..j] do\n    bar[\"sparktext\"..j]:Hide()\n    j = j + 1\nend\n\nif aura_env.config.ticks then\n    if bar then \n        local widthPerSegment = bar:GetHeight() / aura_env.config.maxDur\n        local tickAmount = floor((aura_env.config.maxDur-0.1)/5) \n        \n        local region = WeakAuras.GetRegion(aura_env.id)\n        --[[local textsubtract = 0\n        if aura_env.config.BigIcon.EnableBigIcon == true then \n            textsubtract = 5\n        end\n        ]]\n        local xoffset = 25\n        if aura_env.config.showTickOnLeftSide then\n            xoffset = -xoffset\n        end\n        \n        \n        for i = 1, tickAmount do\n            if not bar[\"spark\"..i] then\n                local spark = bar:CreateTexture(nil, \"ARTWORK\");\n                spark:SetDrawLayer(\"ARTWORK\", 3);\n                bar[\"spark\"..i] = spark\n            end \n            if aura_env.config.tickstext and not bar[\"sparktext\"..i] then \n                local sparktext = bar:CreateFontString(nil,\"ARTWORK\")\n                sparktext:SetDrawLayer(\"ARTWORK\", 3);\n                bar[\"sparktext\"..i] = sparktext\n                \n            end\n            \n            bar[\"spark\"..i]:SetTexture(\"Interface\\\\AddOns\\\\WeakAuras\\\\Media\\\\Textures\\\\Square_White.tga\")\n            bar[\"spark\"..i]:SetWidth(bar:GetWidth());\n            bar[\"spark\"..i]:SetHeight(1);\n            bar[\"spark\"..i]:SetBlendMode(\"ALPHAKEY\");\n            bar[\"spark\"..i]:SetVertexColor(\n                1,\n                1,\n                1,\n                1\n            )\n            bar[\"spark\"..i]:ClearAllPoints()\n            local offset = widthPerSegment*(5*i)\n            bar[\"spark\"..i]:SetPoint(\"BOTTOM\", bar, \"BOTTOM\", 0, offset+0.1)\n            bar[\"spark\"..i]:Show()\n            \n            if aura_env.config.tickstext then\n                bar[\"sparktext\"..i]:SetFont(\"Interface\\\\AddOns\\\\WeakAuras\\\\Media\\\\Fonts\\\\FiraMono-Medium.ttf\",12)  \n                bar[\"sparktext\"..i]:SetText((i*5))\n                bar[\"sparktext\"..i]:ClearAllPoints()\n                bar[\"sparktext\"..i]:SetPoint(\"BOTTOM\", bar, \"Bottom\", xoffset, offset-5)\n                bar[\"sparktext\"..i]:Show()\n            end\n            \n        end\n    end\n    \nend\n\nif DBT and aura_env.config.hideDBMBars then\n    if not DBT:GetSkins().Jods_Raid_Ability_Timeline_Skin then\n        local skin = DBT:RegisterSkin(\"Jods_Raid_Ability_Timeline_Skin\")\n        skin.Options = {\n            HugeAlpha = 0.0001,\n            Alpha = 0.0001,\n            IconLeft = false,\n            IconRight = false,\n            InlineIcons = false,\n            Bar7CustomInline = false,\n            ClickThrough = true,\n            DisableRightClick = true,\n        }\n        if DBT:GetSkins().Jods_Raid_Ability_Timeline_Skin then\n            DBT:SetSkin(\"Jods_Raid_Ability_Timeline_Skin\")\n            DBT:Rearrange()\n        end\n        if AddOnSkins and AddOnSkins[1]:CheckOption('DBM-Core') == true then\n            print(\"Hiding DBM bars while AddonSkins skinning is turned on doesn't work pls either disable the DBM-Core option or the addon and try again\")\n        end\n        \n    end\n    \nelseif DBT and not aura_env.config.hideDBMBars then\n    if DBT:GetSkins().Jods_Raid_Ability_Timeline_Skin then\n        DBT:SetSkin(\"DBM\")\n    end\nend\n\n\nif BigWigsLoader and aura_env.config.hideBWBars then\n    \n    if BigWigsAnchor then\n        \n        C_Timer.After(15, function()\n                \n                BigWigsAnchor:ClearAllPoints()\n                BigWigsAnchor:SetClampedToScreen(false)\n                BigWigsAnchor:SetPoint(\"TOPLEFT\", 9999,9999)\n                BigWigsEmphasizeAnchor:ClearAllPoints()\n                BigWigsEmphasizeAnchor:SetClampedToScreen(false)\n                BigWigsEmphasizeAnchor:SetPoint(\"TOPLEFT\", 9999,9999)\n        end)  \n    elseif not IsAddOnLoaded(\"BigWigs_Options\") then \n        \n        EnableAddOn(\"BigWigs_Core\")\n        LoadAddOn(\"BigWigs_Core\")\n        EnableAddOn(\"BigWigs_Options\")\n        LoadAddOn(\"BigWigs_Options\")\n        BigWigsLoader.RegisterMessage({}, \"BigWigs_CoreEnabled\", function()\n                C_Timer.After(0.5, function()\n                        \n                        BigWigsAnchor:ClearAllPoints()\n                        BigWigsAnchor:SetClampedToScreen(false)\n                        BigWigsAnchor:SetPoint(\"TOPLEFT\", 9999,9999)\n                        BigWigsEmphasizeAnchor:ClearAllPoints()\n                        BigWigsEmphasizeAnchor:SetClampedToScreen(false)\n                        BigWigsEmphasizeAnchor:SetPoint(\"TOPLEFT\", 9999,9999)\n                end)            \n        end)\n    end\n    \n    \n    \n    \n    local JODS_WA_RAT_BWCallbackObj = {}\n    local f = function(event,addon,bar,...) \n        bar:SetAlpha(0)\n        bar:EnableMouse(false)\n        -- For some reason the first ever icon after game launch won't be hidden because it hasn't been initialized properly? Set a short timer to hide it again to work around this.\n        local JODS_BW_BAR_TO_HIDE = bar\n        C_Timer.After(2, function() if (JODS_BW_BAR_TO_HIDE~=nil and JODS_BW_BAR_TO_HIDE:GetAlpha() > 0) then JODS_BW_BAR_TO_HIDE:SetAlpha(0) JODS_BW_BAR_TO_HIDE:EnableMouse(false) end end)\n    end\n    BigWigsLoader.RegisterMessage(JODS_WA_RAT_BWCallbackObj, \"BigWigs_BarCreated\", f);\n    BigWigsLoader.RegisterMessage(JODS_WA_RAT_BWCallbackObj, \"BigWigs_BarEmphasized\", f);\n    \nend",
