["custom"] = "function(event, instanceId)\n    if event == \"AUTOMARK_GET_MOBS\" then \n        local currentInstance = tostring(select(8, GetInstanceInfo())) or C_MythicPlus.GetCurrentSeason()\n        \n        if instanceId ~= currentInstance then\n            -- Player is not in the supported dungeon\n            return\n        end\n        \n        for _, npc in pairs(aura_env.config.mobs) do \n            npc.npcID = npc.npcID:gsub(\"%s+\", \"\") -- Remove spaces\n            \n            for _, id in pairs({ strsplit(\";\", npc.npcID) }) do \n                if npc.autoMarkOption > 1 then \n                    WeakAuras.ScanEvents(\"AUTOMARK_ADD_MOBS\", \n                        tonumber(id), \n                        instanceId, \n                        npc.autoMarkOption, \n                        npc.notAvailable == 2,\n                        npc.markDistance, \n                        npc.unitDied, \n                        npc.makeAvailable,\n                        { \n                            [1] = npc[\"1\"].P1,\n                            [2] = npc[\"1\"].P2,\n                            [3] = npc[\"1\"].P3,\n                            [4] = npc[\"1\"].P4,\n                            [5] = npc[\"1\"].P5,\n                        }, \n                        npc.assignee\n                    )\n                end\n            end\n        end\n    end\nend\n\n-- AUTOMARK_GET_MOBS",
