["init"] = {
["custom"] = "--Usage: drawEjections(number of lines, angle of first line, length of lines)\n--\n-- on Show: drawEjections(5, 0, 100)\n-- on Hide: aura_env.f1:Hide()\n\nfunction aura_env.drawEjections(rays, startangle, length)\n    \n    local cx = aura_env.region.xOffset\n    local cy = aura_env.region.yOffset\n    \n    -- length of rays is a user-configurable % of a base value such as 400\n    aura_env.rayscale = 400 * (aura_env.config.scale / 100)\n    \n    -- create frame in which to draw rays\n    aura_env.f1 = CreateFrame(\"Frame\")\n    aura_env.f1:SetPoint(\"CENTER\", cx, cy)\n    aura_env.f1:SetSize(100,100)\n    \n    -- angle between each ray\n    local slice = 360 / rays\n    \n    local l, x, y, ray\n    local angle = 0\n    \n    -- draw each ray, \"slice\"-degrees apart\n    for ray = 0, rays - 1, 1\n    do\n        -- calculate end point coordinates\n        x = cos(startangle + angle)\n        y = sin(startangle + angle)\n        --print(angle,x,y, x * aura_env.rayscale, y * aura_env.rayscale)\n        \n        -- draw line from center to end point\n        l = aura_env.f1:CreateLine()\n        l:SetColorTexture(0,1,0, aura_env.config.opacity)\n        l:SetStartPoint(\"CENTER\",0,0)\n        l:SetEndPoint(\"CENTER\", x * aura_env.rayscale, y * aura_env.rayscale)\n        \n        -- increment angle for the next ray\n        angle = angle + slice\n        \n    end\n    \n    aura_env.f1:Show()\nend",
["do_custom"] = true,
},
