["customText"] = "function()\n    \n    local goldIcon =  \"|TInterface\\\\MoneyFrame\\\\UI-GoldIcon:12:12:2:0|t\"\n    local silverIcon = \"|TInterface\\\\MoneyFrame\\\\UI-SilverIcon:12:12:2:0|t\"\n    local bronzeIcon = \"|TInterface\\\\MoneyFrame\\\\UI-CopperIcon:12:12:2:0|t\"\n    \n    local iconMap = {\n        gold = goldIcon,\n        silver = silverIcon,\n        bronze = bronzeIcon\n    }\n    \n    local iconNormal, iconAdvanced, iconReversed\n    \n    if aura_env.state then\n        iconNormal = aura_env.state.normal\n        iconAdvanced = aura_env.state.advanced\n        iconReversed = aura_env.state.reversed\n        \n        if (not aura_env.state.index or aura_env.state.index <= 1) then\n            iconNormal = \"N\"\n            iconAdvanced = \"A\"\n            iconReversed = \"R\"\n        elseif (aura_env.state.index > 1) then\n            iconNormal = (iconMap[iconNormal]) or (\"-\")\n            iconAdvanced = (iconMap[iconAdvanced]) or (\"-\")\n            iconReversed = (iconMap[iconReversed]) or (\"-\")\n        end\n        \n        \n    end\n    return iconNormal, iconAdvanced, iconReversed\nend",
