["init"] = {
["custom"] = "aura_env.InspectList = {}\naura_env.GroupList = {}\nC_ChatInfo.RegisterAddonMessagePrefix(\"JODS_INT_UPDATE\")\nC_ChatInfo.RegisterAddonMessagePrefix(\"OmniCD\")\nC_ChatInfo.RegisterAddonMessagePrefix(\"OmniCD2\")\nC_ChatInfo.RegisterAddonMessagePrefix(\"EXRTADD\")\n\nlocal sortOption = aura_env.config.sorting.sortType\naura_env.sortForSettings = function(a,b)\n    if sortOption == 2 then\n        return a<b\n    else\n        return a>b\n        \n    end\nend\n\n\naura_env.CastSuccessModifiers = {\n    [325130] = {spell = 2139,CD = 2.5}\n}\naura_env.InterruptSuccessModifiers = {\n    [78675] = {talent = 202918,CD = 15}, -- balance druid\n    [47528] = {talent = 378848,CD = 3},  -- DeathKnight\n    [2139] = {talent = 382297 ,CD =4 },\n}\n\naura_env.silenceMap = { --interupt:cast\n    [220543] = 15487,  --silence\n    [97547]  = 78675,  --solar beam\n    [93985]  = 106839, --skullbash  \n    [347008] = 89766,  --axe toss Check Zen\n    [132409] = 19647,  --Spell Lock (Sacrifice)\n}\n\naura_env.petSpells = {\n    [19647]  = true, --Spell Lock if used from pet bar\n    [132409] = true, --Spell Lock Command Demon Sacrifice\n    [89766] = true -- axe toss from pet bar]\n    --TODO: DK Leap\n}\n\naura_env.ClassListNew = {\n    -- warrior\n    [71]= {\n        [6552] = {default = 15}},\n    [72]= {\n        [6552] = {default = 15}},\n    [73]= {\n        [6552] = {default = 15},\n        [23920] = {required={[23920] = true},[23920] = 20}, \n        [386071] ={required = {[386071] = true},[386071] = 90}}, \n    -- paladin\n    [65]=  {\n        [96231] = {required = {[96231] = true},[96231] = 15}},\n    [66]= {\n        [96231] = {required = {[96231] = true},[96231] = 15},\n        [31935] = {default = 15}},\n    [70]= {\n        [96231] = {required ={[96231] = true},[96231] = 15}},\n    --HUNTER\n    [253]= {\n        [147362] = {required = {[147362] = true},[147362] = 24},\n        [392060]= {required = {[392060] = true} ,[392060] = 60}},\n    \n    [254]={\n        [147362] = {required = {[147362] = true},[147362] = 24},\n        [392060]= {required ={[392060] = true},[392060] = 60}},\n    [255]= {\n        [187707] = {required = {[187707] = true} ,[187707] = 15}},\n    --ROGUE\"\n    [259]= {\n        [1766] = {default = 15}},\n    [260]= {\n        [1766] = {default = 15}},\n    [261]= {\n        [1766] = {default = 15}},\n    --PRIEST\n    [256]= nil,\n    [257]= nil,\n    [258]= {\n        [15487] = {required = {[15487] = true},[263716] = 15,[15487] = 45}},\n    --DEATHKNIGHT \n    [250]= {\n        [47528] = {required ={[47528] = true} ,[47528] = 15}}, \n    [251]= {\n        [47528] = {required ={[47528] = true},[47528] = 15}},\n    [252]= {\n        [47528] = {required ={[47528] = true},[47528] = 15}},\n    --SHAMAN\n    [262]= {\n        [57994] = {required = {[57994] = true} ,[57994] = 12}},\n    [263]= {\n        [57994] = {required ={[57994] = true},[57994] = 12}},\n    [264]= {\n        [57994] = {required ={[57994] = true}, [57994] = 12}},\n    --MAGE\n    [62]= {\n        [2139] = {default = 24}},\n    [63]= {\n        [2139] = {default = 24}},\n    [64]= {\n        [2139] = {default = 24}},\n    --\"WARLOCK\n    [265]= {\n        [119910] = {default = 24, disabled = {[108503] = true}}, \n        [132409] = {required = {[108503] = true} ,[108503] = 24}},\n    [266]= {\n        [119914] = {default = 30}},\n    [267]= {\n        [119910] = {default = 24, disabled = {[108503] = true}},\n        [132409] = {required = {[108503] = true},[108503] = 24}},\n    --MONK\n    [268]= {\n        [116705] = {required = {[116705] = true},[116705] = 15}},\n    [270]= {\n        [116705] = {required ={[116705] = true},[116705] = 15}},\n    [269]= {\n        [116705] = {required ={[116705] = true},[116705] = 15}},\n    --DRUID\n    [102]= {\n        [78675] = {required ={[78675] = true},[78675] = 60}, \n        [106839] ={required ={[106839] = true},[106839] = 15}},\n    [103]= {\n        [106839] ={required ={[106839] = true},[106839] = 15}},\n    [104]= {\n        [106839] ={required ={[106839] = true},[106839] = 15}},\n    [105]= {\n        [106839] ={required ={[106839] = true},[106839] = 15}},\n    --DEMONHUNTER\n    [577]= {\n        [183752] = {default = 15}},\n    [581]= {\n        [183752] = {default = 15},\n        [202137] = { required = {[202137] = true} , disabled = {[389799] = true},\n            [209281] = 12,[202137] = 60} ,\n        [389809] = { required = {[389799] = true,[202137] = true},\n            [209281] = 12,[202137] = 60, [389799] = 60},\n    },    -- Evoker\n    [1467]= {\n        [351338] = { required = {[351338] = true} , \n            [371016] = 20,[351338] = 40},\n    },\n    [1468]= {\n        [351338] = {required = {[351338] = true},\n            [351338] = 40}\n    },\n    [1473]= {\n        [351338] = { required = {[351338] = true} , \n            [371016] = 20,[351338] = 40},\n    },\n    \n}\naura_env.specialAllowlist = {}\n\nif aura_env.config.enabledInterrupts[\"119910\"] then\n    aura_env.specialAllowlist[132409] = true\nend\n\nif aura_env.config.enabledInterrupts[\"202137\"] then\n    aura_env.specialAllowlist[389809]= true\nend\n\n\n\n\naura_env.spells = {}\naura_env.ClassList = {}\nfor specID, classDetails in pairs(aura_env.ClassListNew) do\n    aura_env.ClassList[specID] = {}\n    for spellID, _ in pairs(classDetails) do\n        aura_env.ClassList[specID][#aura_env.ClassList[specID]+1] = spellID\n        aura_env.spells[spellID] = true\n    end\nend\n\n\nlocal specSpells = aura_env.ClassList[GetSpecializationInfo(GetActiveSpecGroup())]\nif specSpells then\n    for k,v in pairs (specSpells) do\n        WeakAuras.WatchSpellCooldown(v)\n        \n    end\nend\n\naura_env.CreateStates = function(allstates,unit,guid,info) \n    \n    \n    local spell = aura_env.ClassListNew[info.global_spec_id]\n    \n    if spell then\n        for k in pairs(spell) do\n            local Cooldown = spell[k].default or 0\n            if spell[k].required then\n                local disabled = false\n                \n                for key in pairs(spell[k].required) do\n                    Cooldown = spell[k][key]\n                    if not aura_env.InspectList[guid].talents[key] then\n                        disabled = true\n                    end\n                    \n                end\n                if disabled == true then\n                    Cooldown = 0\n                end\n                \n                \n            end\n            \n            for key in pairs (spell[k]) do\n                if key == \"disabled\" then\n                    for disabledSpell in pairs(spell[k][key]) do\n                        if aura_env.InspectList[guid].talents[disabledSpell] then\n                            Cooldown = 0\n                        end\n                    end\n                    \n                    \n                elseif key~= \"required\" and key ~=\"default\" and (not spell[k].required or not spell[k].required[key]) then\n                    if aura_env.InspectList[guid].talents[key] then\n                        Cooldown = Cooldown - spell[k][key]\n                    end\n                end\n                \n            end\n            \n            \n            \n            if Cooldown>0 and (aura_env.config.enabledInterrupts[tostring(k)] or aura_env.specialAllowlist[k])then\n                allstates[guid..\" \".. k] = {\n                    duration = 0,\n                    expirationTime = GetTime(),\n                    progressType = \"timed\",\n                    autoHide = false,\n                    changed = true,\n                    show = true,\n                    unit = unit,\n                    name = UnitName(unit),\n                    spellID = k,\n                    successful = false,    \n                    isDead = false,    \n                    outOfRange = false,\n                    icon = select(3,GetSpellInfo(k)),\n                    class = select(2,UnitClass(unit)),\n                    useBarClassColor = aura_env.config.display.useBarClassColor,\n                    useTextClassColor = aura_env.config.display.useTextClassColor,\n                    guid = guid,\n                    CD = Cooldown\n                }\n            elseif allstates[guid..\" \".. k] then\n                allstates[guid..\" \".. k].changed = true\n                allstates[guid..\" \".. k].show = false\n            end\n        end\n    end\n    \n    \nend\n\n\n\n",
